import React, {useEffect,useContext} from 'react';
import { Formik, Form } from 'formik';
import styled from 'styled-components'
import {AppContext} from '../contexts/AppContext'
import { MyTextInput, MySelect, } from '../components/UIs/FormInputs'
import {reactLocalStorage} from 'reactjs-localstorage';
import * as Yup from 'yup';
import {useNavigate } from 'react-router-dom';

const axios = require('axios')

// And now we can use these
const Login = () => {
  const {
     setUserRole,
     setUser,
     setToken,
     auth

 } = useContext(AppContext);
 const navigate = useNavigate();
  console.log('auth status',auth)
  return (
    <LoginWrapper>
      <h2>Rogin</h2>
      <Formik
        initialValues={{
          username: '',
          password: '',
          role: '',
        }}

        validationSchema={Yup.object({
          username: Yup.string().required('Required'),
          password: Yup.string().required('Required'),
          role: Yup.string().oneOf(['teacher', 'student','admin'],'Invalid Role').required('Required'),
        })}

        onSubmit={(values) => {
        const formParams = {...values}
        axios.post('/auth/signin', formParams)
        .then(function (response) {
          if(response.status === 200){
            setUserRole(response.data.data.role)
            setUser(response.data.data)
            reactLocalStorage.setObject('token',response.data.data.accessToken);
            setToken(response.data.data.accessToken)
          }
        })
        .catch(function (error) {
          console.log(error);
        });
        }}
      
      >
      
   {() => (
      <Form>
        <div className="flex-form">
          <MyTextInput
            label="Username"
            name="username"
            type="text"
            placeholder="username"
          />
          <MyTextInput
            label="Password"
            name="password"
            type="password"
            placeholder="password"
          />
          <MySelect label="Who are you?" name="role">
            <option value="">Select</option>
            <option value="teacher">Teacher</option>
            <option value="student">Student</option>
            <option value="admin">Admin</option>
          </MySelect>
        </div>
      <button className="btn"type="submit">Submit</button>
        </Form>
       )}
      </Formik>
    </LoginWrapper>
  );
};


const LoginWrapper = styled.div`

`

export default Login;
