import React,{useContext, useEffect,useState} from 'react';
import {AppContext} from '../../../contexts/AppContext'
import {PageWrapper,PageView} from "../../../common/DesignConstants"
import PageTitle from "../../UIs/PageTitle"
import AdminStats from "../../Admin/AdminStats"
import AdminProgress from "../../Admin/AdminProgress"
import styled from "styled-components";
const axios = require('axios');


const AdminHome = () => {
  const[data, setData] = useState(false)

  useEffect(() => {
  axios.get(`/get-dashboard/`)
    .then(function (response) {
     if(response.status === 200){
       setData(response.data.data)
      }
    })
    .catch(function (error) {
           console.log(error);
    });
  }, []); 


const {
  user,
  isMobile
 } = useContext(AppContext);
 


 if(!user){
   return <p>Loading...</p>
 }

 return (
  <div>
   <PageWrapper>
     <PageTitle 
      title='Admin Dashboard'
      homePath='dashboard'
      page='Home'
     />
     <PageView>
      <HomeWrapper isMobile={isMobile}>
        <div className="student-stasts">
          <AdminStats data={data}/>
        </div>
        <div className="progress-stasts">
          <AdminProgress data={data}/>
        </div>
        {/* <div className="admin">
           <div className="student-assignments">
             <StudentAssignments courses={user.courses} />
           </div>
           <div className="student-exams">
             <StudentExams courses={user.courses} />
           </div>
        </div> */}
     </HomeWrapper>
     </PageView>
  </PageWrapper>
  </div>
 );
};

const HomeWrapper = styled.div`
.student-upcomings {
    display: flex;
    flex-wrap: wrap;
    flex-wrap: ${({isMobile})=> isMobile ? 'wrap' : 'nowrap'};
    gap: ${({isMobile})=> isMobile ? '15px' : '0px 15px'};
}
.student-exams {
  width: ${({isMobile})=> isMobile ? '100%' : '50%'}
}
.student-assignments {
   width: ${({isMobile})=> isMobile ? '100%' : '50%'}
}
button.nav.default-nav {
    display:${({isMobile})=> isMobile ? 'none' : 'block'} ;
}

.CircularProgressbar {
    width: 150px;
    vertical-align: middle;
}
`

export default AdminHome;
