import React, {useContext} from 'react';
import styled from "styled-components";
import {NavLink } from "react-router-dom";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { solid} from '@fortawesome/fontawesome-svg-core/import.macro'
import {AppContext} from "../../contexts";

const sidebar = () => {
    const {sibarView} = useContext(AppContext)
    console.log(sibarView)
 return (
  <SidebarWrapper>
     <div className="logo">
      <img src="/images/AppLogo.png" alt="logo"/>
     </div>
     <div className="navigation">
      <ul>
       <li>
          <NavLink 
          className={({ isActive }) => isActive ? "activeLink" : ""}
          to='/'
          >
          <span className="icon">
             <FontAwesomeIcon icon={solid('home')} />
          </span>
          <span>Dashboard</span>
         </NavLink>
       </li>
       <li>
          <NavLink 
          className={({ isActive }) => isActive ? "activeLink" : ""}
          to='/student/profile'
          >
          <span className="icon">
             <FontAwesomeIcon icon={solid('user-graduate')} />
          </span>
          <span>Profile</span>
         </NavLink>
       </li>
       <li>
         <NavLink
          className={({ isActive }) => isActive ? "activeLink" : ""}
          to='/student/apply'>
         <span className="icon">
             <FontAwesomeIcon icon={solid('paper-plane')} />
          </span>
          <span>Apply</span>
         </NavLink>
       </li>
       <li>
          <NavLink
          className={({ isActive }) => isActive ? "activeLink" : ""}
          to='/student/applications'>
         <span className="icon">
             <FontAwesomeIcon icon={solid('folder-tree')} />
          </span>
          <span>Applications</span>
          </NavLink>
       </li>
       <li>
          <NavLink
          className={({ isActive }) => isActive ? "activeLink" : ""}
          to='/student/courses'>
            <span className="icon">
               <FontAwesomeIcon icon={solid('layer-group')} />
            </span>
            <span>Courses</span>
          </NavLink>
       </li>
       <li>
          <NavLink
          className={({ isActive }) => isActive ? "activeLink" : ""}
          to='/student/exams'>
            <span className="icon">
               <FontAwesomeIcon icon={solid('folder-open')} />
            </span>
            <span>Exams</span>
          </NavLink>
       </li>
      </ul>
     </div>
  </SidebarWrapper>
 );
};

const SidebarWrapper = styled.div`
 min-width: 20vw;
 background: #f7f7fa;
.logo {
    border-bottom: 1px solid #aaa5a54f;
    img {
    padding: 6px 11px;
    width:61%;
    }
}
.navigation a {
    display: flex;
    align-items: center;
    gap: 0px 9px;
    font-size: 18px;;
    padding: 14px 20px;
    text-decoration: none;
    color: #6f6f6f;
    &:hover {
     border-left: 9px solid #3498ec;
     color: #3498ec;
     transition: border-left 0.2s ease-in-out;
    }
   }
   .navigation li:nth-child(even) {
      background: #efeff5;
   }
   a.activeLink {
    border-left: 9px solid #ffc107 !important;
    color: #ffffff !important;
    background: #3498ec !important;
    transition: border-left 0.2s ease-in-out !important;

   }
   span.icon {
    font-size: 18px;
   }

`

export default sidebar;
