import React from 'react';
import {Link } from "react-router-dom";
import styled from "styled-components";

const Dropdown = ({LogoutUser}) => {
 const sendLogout = () => {
   console.log('logout')
 }
 return (
  <DropdownWrapper>
    <div className="header">
      <img src="" alt='user'/>
      <div>
       <p>username</p>
       <p>role</p>
      </div>
    </div>
    <div className="links">
      <ul>
       <li>
         <Link 
          to='/'
          >MyProfile</Link>
       </li>
       <li>
         <Link 
          to='/'
          >inbox</Link>
       </li>
       <li>
         <span onClick={sendLogout}>Logout</span>
       </li>
      </ul>
    </div>
  </DropdownWrapper>
 );
};

const DropdownWrapper = styled.div`
    position: absolute;
    right: 16px;
    top: 55px;
    background: #d7d6d8;
.header {
    display: flex;
    gap: 5px;
    align-items: center;
}
.links ul li a {
    list-style: none;
    padding: 9px 5px;
    display: block;
    border-top: 1px solid #afafaf4d;
    text-decoration: none;
    color: #000000 !important;
    background: white;
}
.links ul li {
    list-style: none;
}
.links ul li span {
    list-style: none;
    padding: 9px 5px;
    display: block;
    border-top: 1px solid #afafaf4d;
    text-decoration: none;
    color: #000000 !important;
}
.links ul li span:hover {
    cursor: pointer;
    background: #3498ec;
    color: #fff !important;
    
}
.links ul li a:hover {
    cursor: pointer;
    background: #3498ec;
    color: #fff !important;

}

`

export default Dropdown;
