import React from 'react';

const StudentAssignments = ({courses}) => {
 if(courses){
  return<p>Loading...</p>
 }
 const getAssignments = () => {
  let assignments = []
  courses.map((course) => {
    return assignments.push(...course.assignments)
  })
   return assignments
  }
 return (
  <div>
   {getAssignments()[0].course_name}
  </div>
 );
};

export default StudentAssignments;
