import React,{useState} from "react";

export const AppContext = React.createContext(null);

export const AppContextProvider = ({ children }) => {
  const [student, setStudent] = useState([])
  return (
    <AppContext.Provider value={{
     student,
     setStudent
    }}>
      {children}
    </AppContext.Provider>
  );
}
