import React,{useContext} from 'react';
import GlobalStyle from '../common/GlobalStyle'
import styled from "styled-components";
import StudentSidebar from './Sidebars/StudentSidebar'
import StudentRoutes from '../routes/StudentRoutes'
import HeaderBar from './HeaderBar/HeaderBar';
import {AppContext} from "../contexts/AppContext"


const App = () => {
    const {user,setUser,showSmallSidebar,setShowSmallSidebar,mobileNav,setMobileNav} = useContext(AppContext)
return (
  <>
    <GlobalStyle/>
    <AppWrapper>
        <SidebarView>
          <StudentSidebar/>
        </SidebarView>
        <AppView showSmallSidebar={showSmallSidebar}>
          <HeaderBar/>
          <StudentRoutes />
        </AppView>
        {/* <Footer/> */}
    </AppWrapper>
  </>

  );
};

const AppWrapper = styled.div`
    display: flex;
    flex-direction: row;
    height: 100%;
`
const SidebarView = styled.div`
    position: fixed;
    left: 0;
    height: 100vh;
    background: #f7f7fa;

`
const AppView = styled.div`
    width: ${({showSmallSidebarHandler}) => showSmallSidebarHandler ? 'calc(100% - 60px)' : 'calc(100% - 285px)'};
    position: relative;
    left: 285px;
`


export default App;
