import React, {useEffect, useState} from 'react';
import styled from "styled-components";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { solid} from '@fortawesome/fontawesome-svg-core/import.macro'
import moment from 'moment';
import CourseButton from "../UIs/CourseButton"

const axios = require('axios');
const ReviewStudent = ({id,setAdmissionRefresh}) => {
 const[application,setApplication] = useState(false)
 const[refresh,setRefresh] = useState(false)
 const[schoolClasses,setSchoolClasses] = useState(false)


 useEffect(() => {
  axios.get(`/getStudent/${id}`)
   .then(function (response) {
    if(response.status === 200){
      setApplication(response.data.data)
     }
   })
   .catch(function (error) {
          console.log(error);
   });
 }, [id,refresh]); 
 
 useEffect(() => {
  axios.get(`/allclass/`)
   .then(function (response) {
    if(response.status === 200){
      setSchoolClasses(response.data.data)
     }
   })
   .catch(function (error) {
          console.log(error);
   });
 }, []); 

 const approveApplication = (id) => {
   axios.post(`/admitStudent/${id}`)
   .then(function (response) {
    if(response.status === 200){
      setRefresh(true)
      setAdmissionRefresh(true)
     }
   })
   .catch(function (error) {
          console.log(error);
   });
 }
 const approveReject = () => {
   axios.post(`/rejectApplication/${id}`)
   .then(function (response) {
    if(response.status === 200){
      setRefresh(true)
      setAdmissionRefresh(true)
     }
   })
   .catch(function (error) {
          console.log(error);
   });
 }
  
 if(!application){
  return <p>Loading...</p>
 }

 return (
      <ProfileWrapper
        application={application}
        >
           <h2>Student Profile</h2>
           <div className="student-info">
              <div className="student-image">
              </div>
              <div className="information">
                <div className="box-wrapper">
                  <table>
                   <tbody>
                     <tr>
                        <td className="key">Full Name:</td>
                        <td className="value">{application.firstName} {application.lastName}</td>
                     </tr> 
                     <tr>
                        <td className="key">Student Id:</td>
                        <td className="value">{application.studentId}</td>
                     </tr> 
                     <tr>
                        <td className="key">Department:</td>
                        <td className="value">{application.program}</td>
                     </tr> 
                     <tr>
                        <td className="key">Email:</td>
                        <td className="value">{application.email}</td>
                     </tr> 
                     <tr>
                        <td className="key">Address:</td>
                        <td className="value">{application.address}</td>
                     </tr> 
                     <tr>
                        <td className="key">Date of Birth:</td>
                        <td className="value">{moment(application.dob).format('D MMM YYYY')}</td>
                     </tr>
                     </tbody> 
                  </table>
                </div>
              </div>    
           </div>
           <div className="school-info-wrapper">
             <div className="school-info">
               <div className="info-wrapper">
                <span className="title">Motivational Letter</span> 
                <span className="letter">
                  <FontAwesomeIcon icon={solid('file-pdf')} />
                  <a href={application.motivational_letter}>Download</a>
                </span>
               </div>
               <div className="info-wrapper">
                <span className="title">Courses</span>
                <div className="classlist">
                 {
                   application.courses.map(course => <CourseButton course={course}/>)
                 }
                </div>
               </div>
               <div className="info-wrapper">
                 <span className="title"> Add Class</span>
                  <div className="add-class">
                  <p>Add a class </p>
                    <select className="select-class" defaultValue="default" name="class" id="classes" onChange={classSelected}>
                      <option value="default" disabled >Select flight</option>
                        {flights.map(flight => 
                            <option className="options" key={flight._id} value={flight._id} >{flight._id}</option>
                        )}
                    </select>
                  </div>
               </div>
             </div>
           </div>   
        </ProfileWrapper>
 );
};


const ProfileWrapper = styled.div`
.student-image {
    width: 14rem;
    height: 14rem;
    background-position: center;
    background-size: cover;
    border-radius: 7px;
    box-shadow: 0px 0px 5px #0000005e;
    background-image: url(${({application}) => application.user_photo ? `"${application.user_photo}"` : '/images/userimage-holder.png'})
}
.student-info {
    display: flex;
    flex-wrap: wrap;
    align-items: stretch;
    gap: 1rem;
}
.classlist {
    display: flex;
    gap: 5px;
    flex-wrap: wrap;
    align-items: center;
    justify-content: center;
}

.information table {
    height: 100%;
    border-radius: 7px;
    padding: 16px;
}
.box-wrapper {
    height: 100%;
    width: 100%;
    border-radius: 7px;
    box-shadow: 0px 0px 5px #0000005e;
    padding: 16px;
}
td.key {
    color: black;
    font-weight: bold;
}
td.value {
    padding-left: 25px;
    text-transform: capitalize;
}

tbody tr {
    line-height: 1.6;
}

.school-info {
    display: flex;
    gap: 1rem;
    flex-wrap: wrap;
    justify-content: space-around;
}.school-info .info-wrapper {
    background: #3498ec;
    padding: 1rem;
    border-radius: 7px;
    text-align: center;
    text-transform: capitalize;
    color: #fff;
    line-height: 2;
    flex: 1;
}.school-info div span {
    display: block;
}
.school-info-wrapper {
    width: 100%;
    padding: 1.5rem 0;
}
span.title {
    background: #ffc107;
    border-radius: 5px;
    color: black;
    margin-bottom: 25px;
}

button.btn-approve {
    border: none;
    color: #fff;
    background: green;
    padding: 10px 20px;
}
button.btn-rejected {
    border: none;
    color: #fff;
    background: red;
    padding: 10px 20px;
}
button.btn-refuse {
    border: none;
    color: #fff;
    background: #ae1313;
    padding: 10px 20px;
}

span.status {
    background: yellow;
    color: black;
}

button.status {
    background: #ffc107;
    border: none;
    color: #000;
    padding: 10px 20px;
    text-transform: capitalize;
}


button.btn-approve:hover {
    background: #8bc34a;
    color: black;
    transition: all 200ms ease;
    cursor: pointer;
}
button.btn-refuse:hover {
    background: #ff0000;
    color: black;
    transition: all 200ms ease;
    cursor: pointer;
}
.buttons {
    display: flex !important;
    gap: 10px;
    align-items: center;
    justify-content: center;
}
.docs {
    display: flex !important;
}

span.letter {
    display: flex !important;
    align-items: center;
    gap: 7px;
    justify-content: center;
}

span.letter svg {
    color: white;
    font-size: 29px;
}

span.letter a {
    color: #ffc107;
}

span.letter a:hover {
    color: red;
}

button.btn-approved {
    background: #008000;
    border: none;
    color: #fff;
    padding: 10px 20px;
    text-transform: capitalize;
}


`


export default ReviewStudent;
