import React,{useState,useEffect} from "react";
import { useMediaQuery } from 'react-responsive'
import {reactLocalStorage} from 'reactjs-localstorage';
import _ from 'lodash';

export const AppContext = React.createContext(null);

export const AppContextProvider = ({ children }) => {
  const [userAccessGet2, setUserAccessGet2] = useState(
      reactLocalStorage.getObject('userAccess')
  )
  const localUser = reactLocalStorage.getObject('userAccess')
  const [userAccess, SetUserAccess] = useState(localUser)
  const [authGate1, setAuthGate1] = useState(false)
  const [authGate2, setAuthGate2] = useState(_.isEmpty(userAccessGet2) ? false : userAccessGet2)
  const [showSmallSidebar, setShowSmallSidebar] = useState(false)
  const [user, setUser] = useState([])
  const isMobile  = useMediaQuery({ query: '(max-width: 1024px)' })
  const [mobileNav, setMobileNav] = useState(isMobile)
  const [showNav, setShowNav] = useState(false)
  const [logout, setLogout] = useState('loggedin')
  useEffect(() => {
    setMobileNav(isMobile)
  }, [isMobile]);
  return (
    <AppContext.Provider value={{
     showSmallSidebar,
     setShowSmallSidebar,
     user,
     setUser,
     mobileNav,
     setMobileNav,
     showNav,
     setShowNav,
     isMobile,
     authGate1,
     authGate2,
     setAuthGate1,
     setAuthGate2,
     setUserAccessGet2,
     setLogout,
     logout,
     userAccess
    }}>
      {children}
    </AppContext.Provider>
  );
}
