import React from 'react';
import styled from "styled-components";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { solid} from '@fortawesome/fontawesome-svg-core/import.macro'
const CourseButton = ({course}) => {

const getRandomColor = () => {
    var letters = '0123456789ABCDEF';
  var color = '#';
  for (var i = 0; i < 6; i++) {
    color += letters[Math.floor(Math.random() * 16)];
  }
  return color;
 }
 const randomColor = getRandomColor()
 return (
  <CourseButtonWrapper randomColor={randomColor}>
   <FontAwesomeIcon icon={solid('book')} />
   <span>{course.course_code}</span>
   <FontAwesomeIcon className="closing-icon" icon={solid('times-circle')} />
  </CourseButtonWrapper>
 );
};

const CourseButtonWrapper = styled.div`
    display: flex;
    align-items: center;
    justify-content: space-around;
    gap: 7px;
    background: ${({randomColor}) => randomColor} !important;
    padding: 0 5px !important;
    // width: 100px !important;
    font-size: 14px !important;
        border-radius: 5px;
  svg {
    font-size: 150%;
  }

  .closing-icon:hover {
    transform: rotate(180deg);
    color: #3498ec;
    cursor: pointer;
    transition: all 200ms ease;
}
  
  
`
export default CourseButton;
