import React from 'react';
import styled from "styled-components";
import { Slide } from 'react-slideshow-image';
import 'react-slideshow-image/dist/styles.css'
const StudentAssignments = ({courses}) => {
 let newAssignment = []

 if(!courses) {
  return
 }
 const AssignmentList = () => {
  courses.map(course => {
    return course.assignments.map(assignment => {
         return newAssignment.push({...assignment,...course})
         })
  })
  return newAssignment
 }
 console.log(AssignmentList())
 const properties = {
    duration: 1000,
    transitionDuration: 500,
    infinite: true,
    prevArrow: <div style={{width: "30px", marginRight: "-30px"}}><svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 512 512" fill="#fff"><path d="M242 180.6v-138L0 256l242 213.4V331.2h270V180.6z"/></svg></div>,
    nextArrow: <div style={{width: "30px", marginLeft: "-30px"}}><svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 512 512" fill="#fff"><path d="M512 256L270 42.6v138.2H0v150.6h270v138z"/></svg></div>
  };
 return (
  <AssignmentWrapper>
        <Slide {...properties}>
         {newAssignment.map((assignment, index)=> (
            <div className="each-slide" key={index}>
              <div style={{'backgroundImage': 'red'}}>
                <span>{assignment.assignment_code}</span>
              </div>
            </div>
          ))} 
        </Slide>
  </AssignmentWrapper>
 );
};

const AssignmentWrapper = styled.div`
.each-slide > div {
  display: flex;
  align-items: center;
  justify-content: center;
  background-size: cover;
  height: 350px;
}

.each-slide span {
  padding: 20px;
  font-size: 20px;
  background: #efefef;
  text-align: center;
}
`
export default StudentAssignments;
