import React, {useEffect,useContext} from 'react';
import { Formik, Form } from 'formik';
import styled from 'styled-components'
import {AppContext} from '../contexts/AppContext'
import { MyTextInput, MySelect, } from '../components/UIs/FormInputs'
import {reactLocalStorage} from 'reactjs-localstorage';
import * as Yup from 'yup';
import {useNavigate } from 'react-router-dom';

const axios = require('axios')

// And now we can use these
const Login = () => {
  const {
     setUser,
     accessUser,
     setAccessUser,
     auth,
     setAuth

 } = useContext(AppContext);

 const navigate = useNavigate();

  return (
    <LoginWrapper>
      <div className="login-inner-wrapper">
        <h2>Login</h2>
        <Formik
          initialValues={{
            username: '',
            password: '',
            role: '',
          }}

          validationSchema={Yup.object({
            username: Yup.string().required('Required'),
            password: Yup.string().required('Required'),
            role: Yup.string().oneOf(['teacher', 'student','admin'],'Invalid Role').required('Required'),
          })}

          onSubmit={(values) => {
          const formParams = {...values}
          axios.post('/auth/signin', formParams)
          .then(function (response) {
            if(response.status === 200){
              setAuth(true)
              setUser(response.data.data)
              reactLocalStorage.setObject('userAccess',{
              token: response.data.data.accessToken,
              role: response.data.data.role,
              user_id: response.data.data._id,
              });
              setAccessUser(reactLocalStorage.getObject('userAccess'))
              navigate('/')
            }
          })
          .catch(function (error) {
            console.log(error);
          });
          }}
        
        >
        
    {() => (
        <Form>
          <div className="login-form">
            <MyTextInput
              label="Username: "
              name="username"
              type="text"
              placeholder="username"
            />
            <MyTextInput
              label="Password: "
              name="password"
              type="password"
              placeholder="password"
            />
            <MySelect label="Who are you? :" name="role">
              <option value="">Select</option>
              <option value="teacher">Teacher</option>
              <option value="student">Student</option>
              <option value="admin">Admin</option>
            </MySelect>
          </div>
        <button className="login btn"type="submit">Submit</button>
          </Form>
        )}
        </Formik>
      </div>
    </LoginWrapper>
  );
};


const LoginWrapper = styled.div`
    background-image: url(/images/cambridge.jpg);
    height: 100vh;
    width: 100%;
    background-repeat: no-repeat;
    background-size: cover;
    background-position: center;
    display: flex;
    align-items: center;
    justify-content: center;
    &:after {
    content: "";
    background: #01010178;
    height: 100%;
    width: 100%;
    position: absolute;
    left: 0;
    top: 0;
    z-index: 0;
    }
    .login-inner-wrapper {
    z-index: 99;
    text-align: center;
    background: #ffffffb5;
    padding: 0px 30px;
    border-radius: 25px;
    }
    .login-form {
        display: flex;
        flex-direction: column;
        gap: 6px;
    }
    button.login.btn {
    margin: 20px auto;
   }
}
`

export default Login;
