import React from 'react';
import styled from "styled-components";
import AssignmentBox from "./AssingmentBox";
const StudentAssignments = ({courses}) => {

 if(!courses) {
  return
 }
 return (
  <AssignmentWrapper>
   {
    courses.map(course => {
     return course.assignments.map(assignment => {
            return <AssignmentBox key={assignment._id} course={course} assignment={assignment} />
           })
    })
   }
  </AssignmentWrapper>
 );
};

const AssignmentWrapper = styled.div`

`
export default StudentAssignments;
