import React from 'react';
import styled from "styled-components";
import {UserImageBg}  from '../../common/designConstants';
const UserImage = ({user_photo}) => {
 console.log(user_photo)

 return (
  <>
   {   
     !user_photo && (
     <DefaultImageWrapper>
     </DefaultImageWrapper>
     )
  } 
  {
    user_photo && (
    <img style={
     {
      background: `url(${user_photo})`,
      backgroundSize : 'cover',
      width: '40px',
      height: '40px',
      backgroundPosition: 'center'
     }
    } alt="imge"/>
    )
   }
  </>

  
 );
};

const DefaultImageWrapper = styled.div`
    width: 40px;
    height: 40px;
    border-radius: 50%;
    background-position: center;
    background: url('/images/userimage-holder.png');
    background-size: cover;
`
const UserImageWrapper = styled.div`
    width: 40px;
    height: 40px;
    border-radius: 50%;
    background-position: center;
    background:url(${({user_photo}) => true ?  {user_photo} : 'https://api-file-bucket.s3.ca-central-1.amazonaws.com/Felis_silvestris_silvestris_small_gradual_decrease_of_quality.png' });
    background-size: cover;
`
export default UserImage;
