import React,{useContext,useState,useEffect} from 'react';
import styled from "styled-components";
import CourseTableWrapper from "../UIs/CourseTable"
import {AppContext} from '../../contexts/AppContext'
const StudentCourses = () => {
 const {
    user,
 } = useContext(AppContext)
 const[items,setItems] = useState(false)
 useEffect(() => {
  getAssignments()
 }, [user]);
 const courses = user.courses
 if(!user){
  return <p>Loading...</p>
 }

const getAssignments = () => {
  let assignments = []
  courses.map((course) => {
    return assignments.push(...course.assignments)
  })
  setItems(assignments) 
}

 return (
  <StudentCoursesWrapper>
   <div className="wrapper-course">
      <div className="course">
         <CourseTableWrapper title="Lessons" courses={user.courses}/>
      </div>
      <div className="collegues">
       {
        items.map(item => item.instructor)
       }
      </div>
   </div>
  </StudentCoursesWrapper>
 );
};

const StudentCoursesWrapper = styled.div`
.wrapper-course {
    display: flex;
    align-items: center;
    gap: 1rem;
    justify-content: stretch;
}
.collegues {
    background: red;
}

`

export default StudentCourses;
