import styled from "styled-components";
export const COLORS = {
 text: 'black',
 background: 'yellow',
 primary: 'rebeccapurple',
};


export const PageWrapper = styled.div`
    display: block;
    border-radius: 20px;
    padding: 2rem
`
export const PageView = styled.div`
    background-color: #ffffff;
    border-radius: 5px;
    padding: 2rem;
    height: 100%;
    width: 100%;
    display: block;
    box-shadow: 0px 0px 4px 0px #00000047;
    height: 100%;
`
