import React from 'react';
import styled from "styled-components";
const UserImage = ({student_photo}) => {
 console.log()
 return (
  <>
   {   
     !student_photo && (
     <DefaultImageWrapper>
     </DefaultImageWrapper>
     )
  } 
  {
    student_photo && (
    <UserImageWrapper>
    </UserImageWrapper>
    )
   }
  </>

  
 );
};

const DefaultImageWrapper = styled.div`
    width: 40px;
    height: 40px;
    border-radius: 50%;
    background-position: center;
    background: url('/images/userimage-holder.png');
    background-size: cover;
`
const UserImageWrapper = styled.div`
    width: 40px;
    height: 40px;
    border-radius: 50%;
    background-position: center;
    background: url(${({student_photo}) => student_photo});
       transform: ${({open}) => open ? 'translateX(0)' : 'translateX(100%)'};
    background-size: cover;
`
export default UserImage;
