import React, {useContext} from 'react';
import styled from "styled-components";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { solid} from '@fortawesome/fontawesome-svg-core/import.macro';
import {AppContext} from "../../contexts/AppContext"
import image from '../../uploads/00b038bfaae3b8bc7d18564e18d6c260'

const HeaderBar = () => {
    const {student} = useContext(AppContext)
    console.log(student)
    if(student.length === 0) {
      console.log('reached')
      return
    }
    const convertBase64 = (file) => {
      return new Promise((resolve, reject) => {
        const fileReader = new FileReader();
        if (file) {
          fileReader.readAsDataURL(file);
        }
        fileReader.onload = () => {
          resolve(fileReader.result);
        };

        fileReader.onerror = (error) => {
          reject(error);
        };
      });
    };
    const showimage = async (image) => {
      const datz = await convertBase64(image) 
      return datz
    }
    const showit = showimage(student.student_photo.data.data) 
    console.log(showit)
    const imgUrl = `data:image/png;base64,${'/images/00b038bfaae3b8bc7d18564e18d6c260'}`
    // console.log(imgUrl) 
 return (
  <HeaderWrapper>
    <div>
      <img src={`${image}`} alt=""/>
    </div>
    <div className="search-bar">
     <input type="text" placeholder="Search here" name="search">
     </input>
      <FontAwesomeIcon icon={solid('search')} />
    </div>
    <div className="user-area">
      <div className="notification">
        <FontAwesomeIcon icon={solid('bell')} />
      </div>
      <div className="user">
        <img src='/images/userimage-holder.png' alt="user" />
      </div>
    </div>
  </HeaderWrapper>
 );
};
const HeaderWrapper = styled.div`
    display: flex;
    align-items: center;
    border-bottom: 1px solid #aaa5a54f;
    padding: 10px 20px;
    justify-content: space-between;
    .user-area {
    display: flex;
    align-items: center;
    justify-content: center;
    gap: 0px 3rem;
   }
   .user img {
    width: 40px;
   }
   .notification {
       color: #747474;
       font-size: 25px;
   }
   .search-bar input {
    border: none;
    padding: 5px 0px;
}
.search-bar {
    display: flex;
    align-items: center;
    justify-content: space-between;
    background: white;
    padding: 8px 20px;
    border-radius: 25px;
    border: 1px solid #e4e4e4;
    width: 270px;
}
.search-bar input::placeholder {
    color: #7c7c7c;
}
.search-bar input:focus-visible {
    border: none;
    outline: none;
    color: #2196F3;
}
.search-bar svg {
    color: #ffc107;
    font-size: 14px;
}
`
export default HeaderBar;
