import React, {useEffect, useState} from 'react';
import styled from "styled-components";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { solid} from '@fortawesome/fontawesome-svg-core/import.macro'
const axios = require('axios');

const ViewAdmissions = () => {
 const[applications,setApplication] = useState([])

 useEffect(() => {
  axios.get('http://localhost:4000/getApplications')
   .then(function (response) {
    if(response.status === 200){
      setApplication(response.data.data)
     }
   })
   .catch(function (error) {
          console.log(error);
   });
 }, []);

 const pagePerRow = () => {
  console.log('clicked')
 }
 const nextRows = () => {
  console.log('clicked')
 }

 if(applications.length <= 0){
  return <p>Loading...</p>
 }

 console.log(applications)
return (
    <ViewApplications>
     <table>
       <thead>
          <tr>
            <th>Student Name</th>
            <th>Program</th>
            <th>Semester</th>
            <th>Status</th>
            <th>Action</th>
          </tr>
      </thead>
      <tbody>
      {
        applications.map((application) => {
          return (
         <tr className="record" key={application._id}>
             <td>{application.firstName} {application.lastName}</td>
             <td>{application.program}</td>
             <td>{application.semester}</td> 
             <td>{application.approved ? 'Approved' : 'Pending'}</td> 
             <td>
              <button>Review</button>
             </td> 
         </tr>
          )
        })
      }
      <tr>
       <td>
        <div className="pages-wrapper">
           <div className="page-select">
            <p>Rows Per Page: </p>
             <form className="pic-page" onSubmit={pagePerRow}>
              <select name="pages" id="pages">
                <option value="5">5</option>
                <option value="10">10</option>
                <option value="20">20</option>
                <option value="30">30</option>
              </select>
             </form>
           </div>
           <div className="next-button">
              <FontAwesomeIcon onClick={nextRows} icon={solid('angle-right')} />
              <div>page1</div>
              <FontAwesomeIcon onClick={nextRows} icon={solid('angle-left')} />
           </div>
        </div>
       </td>
      </tr>
      </tbody>
     </table>
   </ViewApplications>
 );
};

const ViewApplications = styled.div`
overflow: scroll;
td, th {
    border: 1px solid #ffffff;
    text-align: left;
    padding: 8px;
    text-transform: capitalize;
}
tr:nth-child(even) {
    background-color: #ffc107;
}
table {
    border-collapse: collapse;
    width: 100%;
}
tr.record {
    background: #3498ec;
    color: #ffffff;
}
th {
    background: #ffc107;
}

`

export default ViewAdmissions;
