import React, {useEffect, useState} from 'react';
import styled from "styled-components";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { solid} from '@fortawesome/fontawesome-svg-core/import.macro'
const axios = require('axios');

const ViewAdmissions = () => {
 const[applications,setApplication] = useState([])
 const[page,setPage] = useState(1)
 const[limit,setLimit] = useState(4)
 useEffect(() => {
  axios.get(`http://localhost:4000/getApplications?page=${page}&limit=${limit}`)
   .then(function (response) {
    if(response.status === 200){
      setApplication(response.data.data)
     }
   })
   .catch(function (error) {
          console.log(error);
   });
 }, [page,limit]);

 const pagePerRow = (e) => {
     e.preventDefault();
     console.log(e.target.value)
     const pagePerLimit = e.target.value
     setLimit(pagePerLimit)
 }
 const nextRows = () => {
  setPage(page + 1)
 }
 const prevRows = () => {
  if(page <= 1) {
   return setPage(1)
  }
  setPage(page - 1)
 }

 if(applications.length <= 0){
  return <p>Loading...</p>
 }

 console.log(applications)
return (
    <ViewApplications>
     <table>
       <thead>
          <tr>
            <th>Student Name</th>
            <th>Program</th>
            <th>Semester</th>
            <th>Status</th>
            <th>Action</th>
          </tr>
      </thead>
      <tbody>
      {
        applications.map((application) => {
          return (
         <tr className="record" key={application._id}>
             <td>{application.firstName} {application.lastName}</td>
             <td>{application.program}</td>
             <td>{application.semester}</td> 
             <td>{application.approved ? 'Approved' : 'Pending'}</td> 
             <td>
              <button>Review</button>
             </td> 
         </tr>
          )
        })
      }
      </tbody>
     </table>
     <div className="pages-wrapper">
        <div className="page-select">
         <p>Rows Per Page: </p>
           <select name="pages"  defaultValue={limit}  id="pages" onChange={pagePerRow}>
             <option value={limit}>{limit}</option>
             <option value="5">5</option>
             <option value="1">1</option>
             <option value="2">2</option>
             <option value="3">3</option>
           </select>
        </div>
        <div className="next-button">
           <div className="icon" onClick={prevRows}>
             <FontAwesomeIcon icon={solid('angle-left')} />
           </div>
           <div>Page {page}</div>
           <div className="icon" onClick={nextRows}>
           <FontAwesomeIcon icon={solid('angle-right')} />
           </div>
        </div>
     </div>
   </ViewApplications>
 );
};

const ViewApplications = styled.div`
    overflow: scroll;
    &::-webkit-scrollbar {
      display: none;
    }
    -ms-overflow-style: none;  /* IE and Edge */
    scrollbar-width: none;  /* Firefox */
td, th {
    border: 1px solid #ffffff;
    text-align: left;
    padding: 8px;
    text-transform: capitalize;
}
tr:nth-child(even) {
    background-color: #ffc107;
}
table {
    border-collapse: collapse;
    width: 100%;
}
tr.record {
    background: #3498ec;
    color: #ffffff;
}
th {
    background: #ffc107;
}
.pages-wrapper {
    display: flex;
    align-items: center;
    padding: 12px 0px;
    gap: 0.5rem;
}

.page-select {
    display: flex;
    align-items: center;
    gap: 0.5rem;
}

.next-button {
    display: flex;
    align-items: center;
    gap: 0.5rem;
}
form.pic-page {
    padding: 0px;
}

.icon {
    background: #3498ec;
    padding: 5px 13px;
    color: white;
}

.icon:hover {
    background: #ffc107;
    cursor: pointer;
    transition: all 200ms ease;
}

.icon:hover svg {
    transform: rotate(360deg);
    transition: all 200ms ease;
}
select#pages {
    background: #ffffff;
    padding: 2px 0px;
    border: 2px solid #3498ec;
    border-radius: 3px;
}
select#pages:focus {
    outline: none;
}

`

export default ViewAdmissions;
