import React, {useContext,useEffect,useState} from 'react';
import {PageWrapper,PageView} from "../../../common/DesignConstants"
import PageTitle from "../../UIs/PageTitle"
import styled from "styled-components";
import TeacherStats from '../../Teacher/TeacherStats'
import {AppContext} from '../../../contexts/AppContext'
const axios = require('axios');
const TeacherHome = () => {
const[data, setData] = useState(false)
const {
  user,
  isMobile
 } = useContext(AppContext);
 
useEffect(() => {
  axios.get(`/get-teacher-dashboard/6275ec8fe2e9df524f5ec151`)
    .then(function (response) {
     if(response.status === 200){
       setData(response.data.data)
      }
    })
    .catch(function (error) {
           console.log(error);
    });
  }, []); 

 return (
  <PageWrapper>
   <PageTitle 
    title='Teacher Dashboard'
    homePath='dashboard'
    page='home'
   />
   <PageView>
      <HomeWrapper isMobile={isMobile}>
         <div className="student-stasts">
            <TeacherStats data={data}/>
         </div>
         {/* <div className="student-stasts">
            <AdminStats data={data}/>
         </div> */}
      </HomeWrapper>
   </PageView>
  </PageWrapper>
 );
};


const HomeWrapper = styled.div`

`

export default TeacherHome;
