import React from 'react';
import styled from "styled-components";
import { Slide } from 'react-slideshow-image';
import 'react-slideshow-image/dist/styles.css'
const StudentAssignments = ({courses}) => {
 let newAssignment = []

 if(!courses) {
  return
 }
 const AssignmentList = () => {
  courses.map(course => {
    return course.assignments.map(assignment => {
         return newAssignment.push({...assignment,...course})
         })
  })
  return newAssignment
 }
 console.log(AssignmentList())
 return (
  <AssignmentWrapper>
        <Slide duration="1000">
         {newAssignment.map((assignment, index)=> (
            <div className="each-slide" key={index}>
              <div style={{'backgroundImage': 'red'}}>
                <span>{assignment.assignment_code}</span>
              </div>
            </div>
          ))} 
        </Slide>
  </AssignmentWrapper>
 );
};

const AssignmentWrapper = styled.div`
.each-slide > div {
  display: flex;
  align-items: center;
  justify-content: center;
  background-size: cover;
  height: 350px;
}

.each-slide span {
  padding: 20px;
  font-size: 20px;
  background: #efefef;
  text-align: center;
}
`
export default StudentAssignments;
