import React,{useContext,useState,useEffect} from 'react';
import styled from "styled-components";
import CourseTableWrapper from "../UIs/CourseTable"
import StudentAssignments from "../UIs/StudentAssignments"
import Slideshow from "../UIs/Slider"
import {AppContext} from '../../contexts/AppContext'
const StudentCourses = () => {
 const {
  user,
  isMobile
 } = useContext(AppContext);

  if(!user) {
  return
 }
 return (
  <StudentCoursesWrapper
  isMobile ={isMobile}>
   <div className="wrapper-course">
      <div className="course">
         <CourseTableWrapper title="Lessons" courses={user.courses}/>
      </div>
      <div className="assignment">
         <StudentAssignments courses={user.courses}/>
      </div>
   </div>
  </StudentCoursesWrapper>
 );
};

const StudentCoursesWrapper = styled.div`
.wrapper-course {
    display: flex;
    flex-wrap: wrap;
    align-items: center;
    gap: 1rem;
    justify-content: stretch;
}
.course {
 width: ${({isMobile}) => isMobile ? '100%' : '500px'};
 align-self: stretch;
}
.assignment {
  width: ${({isMobile}) => isMobile ? '100%' : 'calc(100% - 500px)'};
  align-self: stretch;
}
.react-slideshow-container+ul.indicators {
    gap: 20px;
}
.react-slideshow-container+ul.indicators .each-slideshow-indicator:before {
    position: absolute;
    top: 0;
    left: 0;
    width: 10px;
    height: 10px;
    border-radius: 50%;
    content: '';
    background: #3498ec;
    text-align: center;
}



`

export default StudentCourses;
