import React, {useContext} from 'react';
import styled from "styled-components";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { solid} from '@fortawesome/fontawesome-svg-core/import.macro';
import {AppContext} from "../../contexts/AppContext"

const HeaderBar = (student_photo) => {
    const {student} = useContext(AppContext)
    
    if(student.length === 0) {
      return
    }
    // const student_photo = student.student_photo
 


 return (
  <HeaderWrapper>
    <div className="search-bar">
     <input type="text" placeholder="Search here" name="search">
     </input>
      <FontAwesomeIcon icon={solid('search')} />
    </div>
    <div className="user-area">
      <div className="notification">
        <FontAwesomeIcon icon={solid('bell')} />
      </div>
      <div className="user">

        {/* {
          student.student_photo && (
            <img src={`${student.student_photo}`} alt="user_image"/>
          )
        }
        {
          !student.student_photo && (
            <img src='/images/userimage-holder.png' alt="user" />
          )
        } */}
      </div>
    </div>
  </HeaderWrapper>
 );
};
const HeaderWrapper = styled.div`
    display: flex;
    align-items: center;
    border-bottom: 1px solid #aaa5a54f;
    padding: 10px 20px;
    justify-content: space-between;
    .user-area {
    display: flex;
    align-items: center;
    justify-content: center;
    gap: 0px 3rem;
   }
   .user {
    width: 40px;
    height: 40px;
    border-radius: 50%;
    background-position: center;
    background-size: cover;
    background: url(${({student_photo}) => student_photo ? student_photo : '/images/userimage-holder.png'});
   }
   .notification {
       color: #747474;
       font-size: 25px;
   }
   .search-bar input {
    border: none;
    padding: 5px 0px;
}
.search-bar {
    display: flex;
    align-items: center;
    justify-content: space-between;
    background: white;
    padding: 8px 20px;
    border-radius: 25px;
    border: 1px solid #e4e4e4;
    width: 270px;
}
.search-bar input::placeholder {
    color: #7c7c7c;
}
.search-bar input:focus-visible {
    border: none;
    outline: none;
    color: #2196F3;
}
.search-bar svg {
    color: #ffc107;
    font-size: 14px;
}
`
export default HeaderBar;
