import React,{useState} from "react";

export const AppContext = React.createContext(null);

export const AppContextProvider = ({ children }) => {

  const small_sidebar = true
  const [user, setUser] = useState([])
  return (
    <AppContext.Provider value={{
     small_sidebar,
     user,
     setUser
    }}>
      {children}
    </AppContext.Provider>
  );
}
