import React from 'react';
import styled from "styled-components";
import Slideshow from "./AssingmentSlider";
const StudentAssignments = ({courses}) => {

 if(!courses) {
  return <p>Loading...</p>
 }
 const AssignmentList = () => {
   return courses.map(course => {
     return course.assignments.map(assignment => {
            return <Slideshow key={assignment._id} course={course} assignment={assignment} />
           })
    })
 }
 console.log('AssignmentList',AssignmentList)
 return (
  <AssignmentWrapper>
     <p>list</p>
  </AssignmentWrapper>
 );
};

const AssignmentWrapper = styled.div`

`
export default StudentAssignments;
