import { createGlobalStyle } from "styled-components";


export default createGlobalStyle`

  html,body {

      * {
        padding: 0;
        margin: 0;
        box-sizing: border-box;
        font-family: 'Inter', sans-serif;
        h1,h2 {
            font-size: 30px;
            margin: 10px 0px;
        }
      }
  }

  // ************************formik styling**********************************
  // ************************************************************************
  form {
    padding: 1rem;
  }
  .flex-form {
    display: flex;
    flex-wrap: wrap;
    gap: 1rem;
    border: 1px solid #e1e1e1f7;
    padding: 2rem;
}.inputBloack {
    display: flex;
    align-items: center;
    justify-content: space-evenly;
}.inputBloack {
    display: flex;
    font-weight: bold;
    word-break: keep-all;
    gap: 5px;
}.react-datepicker-wrapper {
    width: auto !important;
}.inputBloack input {
    background: #e6e6e6ab;
    border: none;
    padding: 7px 3px;
    border-radius: 5px;
}

.inputBloack select {
    background: #e6e6e6ab;
    border: none;
    border-radius: 5px;
    padding: 7px 3px;
}
.file_input {
    background: none !important;
}
button.btn {
    display: block;
    padding: 1rem 4rem;
    border: none;
    background: #2196f3;
    color: #fff;
    border-radius: 7px;
    box-shadow: 0px 0px 5px #000000bf;
    margin: 2rem 0;
   font-weight: bold;
    &:hover {
        color: #000000;
        background: #ffc107;
        transition: all 300ms ease;
    }
}


// ************************formik styling**********************************
// ************************************************************************

`;
