import React from 'react';
import {Link } from "react-router-dom";
import styled from "styled-components";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { solid} from '@fortawesome/fontawesome-svg-core/import.macro';
import moment from 'moment';

const DropdownNotification = ({announcement}) => {

 return (
  <DropdownWrapper>
   <div className="announcement-map">
    {
     announcement.map((announce) => {
       return (
        <div className="announce-info">
          <div className="info">
           <p className="title">{announce.announcement}</p>
           <p className="date">{moment(announce.date).format('D MMM YYYY')}</p>
          </div>
          <div className="file">
           <a href={announce.announcement_file}>
            <FontAwesomeIcon icon={solid('file-pdf')} />
           </a>
          </div>
        </div>
       )
     })
    }
   </div>
  </DropdownWrapper>
 );
};

const DropdownWrapper = styled.div`

    position: absolute;
    right: 0px;
    background: white;
    top: 45px;
    border: 1px solid #b3b3b357;
    border-radius: 4px;


.announce-info {
    display: flex;
    align-items: center;
    justify-content: space-between;
    border-top: 1px solid #18181821;
    padding: 5px;
    gap: 1rem;
}

.info {
    display: flex;
    align-items: flex-start;
    justify-content: center;
    flex-direction: column;
}

p.title {
    font-size: 20px;
    white-space: nowrap;
    color: #000;
}

p.date {
    font-size: 15px;
}

.file a {
    font-size: 38px;
    color: #3498ec;
}

`

export default DropdownNotification;
