import React, {useEffect, useState} from 'react';
import styled from "styled-components";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { solid} from '@fortawesome/fontawesome-svg-core/import.macro'
const axios = require('axios');

const ViewAdmissions = () => {
 const[applications,setApplication] = useState([])
 useEffect(() => {
  axios.get('http://localhost:4000/getApplications')
   .then(function (response) {
    if(response.status === 200){
      setApplication(response.data.data)
     }
   })
   .catch(function (error) {
          console.log(error);
   });
 }, []);
 if(applications.length <= 0){
  return <p>Loading...</p>
 }
 console.log(applications)
return (
    <ViewApplications>
     <table>
       <thead>
          <tr>
            <th>Student Name</th>
            <th>Program</th>
            <th>Semester</th>
            <th>Motivational Letter</th>
            <th>Status</th>
          </tr>
      </thead>
      <tbody>
      {
         applications.length > 0 && 
         (applications.studentApplication_id.map((application) => {
          return (
         <tr className="record" key={application}>
             <td>{application.firstName} {application.lastName}</td>
             <td>{application.program}</td>
             <td>{application.semester}</td> 
             <td>
              <a href={application.motivational_letter}>
                <FontAwesomeIcon icon={solid('file-pdf')} />
                <span>Download</span> 
                </a>
             </td> 
             <td>{application.semester ? 'Approved' : 'Not Approved'}</td> 
         </tr>
          )
        })
       )
      }
      </tbody>
     </table>
   </ViewApplications>
 );
};

const ViewApplications = styled.div`
td, th {
    border: 1px solid #ffffff;
    text-align: left;
    padding: 8px;
    text-transform: capitalize;
}
tr:nth-child(even) {
    background-color: #ffc107;
}
table {
    border-collapse: collapse;
    width: 100%;
}
tr.record {
    background: #3498ec;
    color: #ffffff;
}
th {
    background: #ffc107;
}

`

export default ViewAdmissions;
