import React from 'react';
import styled from "styled-components";
const StudentStats = () => {
 return (
  <StudentStatsWrapper>
    <div className="StudentStats">
      <div className="totalClasses stats-box">
        <div className="theicon">
         icon
        </div>
        <div className="numbers">
          <span>04/06</span>
          <span>Total Classes</span>
        </div>
      </div>
      <div className="total-assignments stats-box">
        <div className="theicon">
         icon
        </div>
        <div className="numbers">
          <span>40/60</span>
          <span>Total Assignments</span>
        </div>
      </div>
      <div className="fees-due stats-box">
        <div className="theicon">
         icon
        </div>
        <div className="numbers">
          <span>$4502</span>
          <span>Fees Due</span>
        </div>
      </div>
    </div>
  </StudentStatsWrapper>
 );
};


const StudentStatsWrapper = styled.div`
.StudentStats {
    display: flex;
    align-items: center;
    padding: 20px 0px;
    justify-content: space-around;
    flex-wrap: wrap;
    gap: 0px 10px;
}
.stats-box {
    flex: 1;
    border-radius: 30px 0px;
    padding: 20px;
    display: flex;
    gap: 10px;
    flex-wrap: wrap;
}
.totalClasses {
    background-color: rgb(244 104 65 / 0.2);
    border-color: #f46841;
    border-radius: 30px 0px;
    border: 1px solid #f46841;
}
.total-assignments {
    background-color: rgb(253 187 56 / 0.5);
    border-color: #fdbb38;
    border-radius: 30px 0px;
    border: 1px solid #fdbb38;
}
.fees-due {
    background-color: rgb(110 107 250 / 0.2);
    border-color: #6e6bfa;
    border-radius: 30px 0px;
    border: 1px solid #6e6bfa;
}
.numbers {
    display: flex;
    flex-direction: column;
}


`
export default StudentStats;
