import React from 'react';
import styled from "styled-components";
import { Slide } from 'react-slideshow-image';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { solid} from '@fortawesome/fontawesome-svg-core/import.macro'
import 'react-slideshow-image/dist/styles.css'
import moment from 'moment';
const StudentAssignments = ({courses}) => {
 let newAssignment = []

 if(!courses) {
  return
 }
 const AssignmentList = () => {
  courses.map(course => {
    return course.assignments.map(assignment => {
         return newAssignment.push({...assignment,...course})
         })
  })
  return newAssignment
 }
 console.log(AssignmentList())
 const properties = {
    duration: 30000,
    autoPlay:false,
    transitionDuration: 500,
    infinite: true,
    indicators: true,
  };
  const getRandomColor = () => {
    var lum = -0.25;
    var hex = String('#' + Math.random().toString(16).slice(2, 8).toUpperCase()).replace(/[^0-9a-f]/gi, '');
    if (hex.length < 6) {
        hex = hex[0] + hex[0] + hex[1] + hex[1] + hex[2] + hex[2];
    }
    var rgb = "#",
        c, i;
    for (i = 0; i < 3; i++) {
        c = parseInt(hex.substr(i * 2, 2), 16);
        c = Math.round(Math.min(Math.max(0, c + (c * lum)), 255)).toString(16);
        rgb += ("00" + c).substr(c.length);
    }
    return rgb;
 }
 return (
  <AssignmentWrapper >
        <Slide {...properties}>
         {newAssignment.map((assignment, index)=> (
            <div className="each-slide" key={index}>
              <div className="assignment-part" style={{'background': getRandomColor()}}>
                <div className="assignment-header">
                 <span className="course">Course: {assignment.course_name}</span>
                 <span>||</span>
                 <span>{assignment.assignment_name}</span>
                </div>
                <div className="assignment-info">
                  <FontAwesomeIcon className="assignment-icon" icon={solid('book-reader')} />
                  <span className="letter">
                    <FontAwesomeIcon icon={solid('file-pdf')} />
                    <a href={assignment.assignment}>assignment</a>
                  </span>
                </div>
                <div className="footer-wrapper">
                <span className="due">
                  <FontAwesomeIcon className="assignment-icon" icon={solid('alarm-clock')} />
                   <span>Due: {moment(assignment.due_date).format('D MMM YYYY')}</span>
                </span>
                <span className="instructor">Instructor: {assignment.instructor}</span>
                </div>
              </div>
            </div>
          ))} 
        </Slide>
  </AssignmentWrapper>
 );
};

const AssignmentWrapper = styled.div`
 .assignment-part {
    text-align: center;
    color: #fff;
    padding: 1rem;
    text-transform: capitalize;
    border-radius: 14px;
}

.assignment-header {
    display: flex;
    align-items: center;
    justify-content: space-evenly;
    padding: 5px;
    background: #ffc107;
    margin: 5px 0px;
    color: black;
    gap: 10px;
    font-size: 14px;
}
.footer-wrapper {
    display: flex;
    flex-direction: row;
    gap: 10px;
    justify-content: space-between;
    align-items: center;
}

.assignment-info {
    padding: 2rem 0px;
    display: flex;
    justify-content: center;
    gap: 10px;
    font-size: 30px;
    align-items: center;
}

span.letter {
    background: #3498ec;
    border: 1px solid;
    display: flex;
    gap: 5px;
    align-items: center;
    padding: 5px;
    color: #fff !important;
    border-radius: 11px;
    a {
    color: #fff;
    }
}
.assignment-icon {
    font-size: 3rem;
}
span.due {
    background: #4caf50;
    box-shadow: 3px 3px black;
    border-radius: 5px;
    padding: 10px;
}


`
export default StudentAssignments;
