import React, {useContext} from 'react';
import styled from "styled-components";
import {NavLink } from "react-router-dom";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { solid} from '@fortawesome/fontawesome-svg-core/import.macro'
import {AppContext} from "../../contexts/AppContext";

const Sidebar = ({showSmallSidebar}) => {
 console.log( showSmallSidebar)
 
 return (
  <SidebarWrapper showSmallSidebar={showSmallSidebar}>
     <div className="logo">
      {!showSmallSidebar &&
       <img src="/images/AppLogo.png" alt="logo"/>
      }
      {showSmallSidebar &&
       <img src="/images/AppLogo_small.png" alt="logo"/>
      }
     </div>
     <div className="navigation">
      <ul>
       <li>
          <NavLink 
          className={({ isActive }) => isActive ? "activeLink" : ""}
          to='/'
          >
          <span className="icon">
             <FontAwesomeIcon icon={solid('home')} />
          </span>
          <span className="navigation_item">Dashboard</span>
         </NavLink>
       </li>
       <li>
          <NavLink 
          className={({ isActive }) => isActive ? "activeLink" : ""}
          to='/student/profile'
          >
          <span className="icon">
             <FontAwesomeIcon icon={solid('user-graduate')} />
          </span>
          <span className="navigation_item">Profile</span>
         </NavLink>
       </li>
       <li>
         <NavLink
          className={({ isActive }) => isActive ? "activeLink" : ""}
          to='/student/apply'>
         <span className="icon">
             <FontAwesomeIcon icon={solid('paper-plane')} />
          </span>
          <span className="navigation_item">Apply</span>
         </NavLink>
       </li>
       <li>
          <NavLink
          className={({ isActive }) => isActive ? "activeLink" : ""}
          to='/student/applications'>
         <span className="icon">
             <FontAwesomeIcon icon={solid('folder-tree')} />
          </span>
          <span className="navigation_item">Applications</span>
          </NavLink>
       </li>
       <li>
          <NavLink
          className={({ isActive }) => isActive ? "activeLink" : ""}
          to='/student/courses'>
            <span className="icon">
               <FontAwesomeIcon icon={solid('layer-group')} />
            </span>
            <span className="navigation_item">Courses</span>
          </NavLink>
       </li>
       <li>
          <NavLink
          className={({ isActive }) => isActive ? "activeLink" : ""}
          to='/student/exams'>
            <span className="icon">
               <FontAwesomeIcon icon={solid('folder-open')} />
            </span>
            <span className="navigation_item">Exams</span>
          </NavLink>
       </li>
      </ul>
     </div>
  </SidebarWrapper>
 );
};

const SidebarWrapper = styled.div`
 min-width: 20vw;
 min-width: ${({showSmallSidebar}) => showSmallSidebar ? 'auto' : '20vw'};
 transition: min-width 1s ease-in-out;
 background: #f7f7fa;
 span.navigation_item {
 display: ${({showSmallSidebar}) => showSmallSidebar ? 'none' : 'block'};
}
.logo {
    border-bottom: 1px solid #aaa5a54f;
    img {
    padding: 6px 11px;
    width:61%;
    }
}
.navigation a {
    display: flex;
    align-items: center;
    gap: 0px 9px;
    font-size: 18px;;
    padding: 14px 20px;
    text-decoration: none;
    color: #6f6f6f;
    &:hover {
     border-left: 9px solid #3498ec;
     color: #3498ec;
     transition: border-left 0.2s ease-in-out;
    }
   }
   .navigation li:nth-child(even) {
      background: #efeff5;
   }
   a.activeLink {
    border-left: 9px solid #ffc107 !important;
    color: #ffffff !important;
    background: #3498ec !important;
    transition: border-left 0.2s ease-in-out !important;

   }
   span.icon {
    font-size: 18px;
   }

`

export default Sidebar;
